{
  "assessment_methods": {
    "practical_performance": {
      "name": "Practical Performance (PP)",
      "description": "A practical Performance (PP) assessment will provide direct evidence of whether candidates have acquired the competency for the ability statements by solving a scenario-based problem.",
      "focus": "The Practical Performance (PP) assessment focuses on providing authentic 'Show Me Application' evidence of candidates' ability to apply GitHub Copilot in programming tasks.",
      "tasks": [
        "Candidates will complete practical coding tasks using GitHub Copilot to demonstrate their ability to enhance programming workflows."
      ],
      "evidence": {
        "LO1": "Candidates will identify gaps in existing workflows and propose AI-assisted solutions using GitHub Copilot.",
        "LO2": "Candidates will apply GitHub Copilot to streamline coding processes in various programming languages."
      },
      "submission": [
        "Candidates will submit their code files and documentation detailing how they utilized GitHub Copilot to enhance their programming efficiency.",
        "This includes annotated code snippets showing Copilot's contributions to task completion."
      ],
      "marking_process": [
        "Effectiveness in Identifying Gaps.",
        "Quality of Proposed Solutions.",
        "Application of GitHub Copilot."
      ],
      "retention_period": "All submitted evidence, including code files and assessment records, will be retained for 3 years to ensure compliance with institutional policies and for auditing purposes."
    }
  }
}

